     function convertirRespuesta($alternativa){

        $respuesta = '';

        if($alternativa == 1){
            $respuesta= 'Si';
        }
        if($alternativa == 2){
            $respuesta= 'No';
        }
        if($alternativa == 3){
            $respuesta= 'NA';
        }

        return $respuesta;
    }


